import React, { useEffect, useState } from 'react';
import Web3 from 'web3';
import detectEthereumProvider from '@metamask/detect-provider';
import SupplyChain from './contracts/SupplyChain.json';
import Navbar from './components/navbar';

function App() {
  const [account, setAccount] = useState('');
  const [contract, setContract] = useState(null);
  const [productName, setProductName] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productType, setProductType] = useState('');
  const [serialNumber, setSerialNumber] = useState('');
  const [manufacturer, setManufacturer] = useState('');
  const [certification, setCertification] = useState('');
  const [warranty, setWarranty] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    loadWeb3();
    loadBlockchainData();
  }, []);

  // Load Web3 and connect MetaMask
  const loadWeb3 = async () => {
    const provider = await detectEthereumProvider();

    if (provider) {
      console.log("MetaMask detected");
      window.web3 = new Web3(provider);
      try {
        const accounts = await provider.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]); // Set the first account
        console.log("Account connected: ", accounts[0]);
      } catch (error) {
        console.error("User denied account access", error);
      }
    } else {
      alert('Please install MetaMask to interact with this DApp!');
    }
  };

  // Load blockchain data (contract, products)
  const loadBlockchainData = async () => {
    const web3 = window.web3;

    const accounts = await web3.eth.getAccounts();
    setAccount(accounts[0]);

    const networkId = await web3.eth.net.getId();
    const networkData = SupplyChain.networks[networkId];

    if (networkData) {
      const supplyChain = new web3.eth.Contract(SupplyChain.abi, networkData.address);
      setContract(supplyChain);
      console.log("Contract loaded: ", supplyChain);

      const productCount = await supplyChain.methods.productCounter().call();
      const loadedProducts = [];
      for (let i = 1; i <= productCount; i++) {
        const product = await supplyChain.methods.products(i).call();
        loadedProducts.push(product);
      }
      setProducts(loadedProducts);
    } else {
      alert('Smart contract not deployed to the detected network.');
    }
  };

  // Create a new product (with electronics-specific data)
  const createProduct = async () => {
    if (contract) {
      try {
        console.log("Attempting to create product with the following details:");
        console.log("Name:", productName);
        console.log("Description:", productDescription);
        console.log("Price:", productPrice);
        console.log("Type:", productType);
        console.log("Serial Number:", serialNumber);
        console.log("Manufacturer:", manufacturer);
        console.log("Certification:", certification);
        console.log("Warranty:", warranty);

        // Send transaction
        await contract.methods
          .createProduct(
            productName,
            productDescription,
            Web3.utils.toWei(productPrice, 'Ether'),
            productType,
            serialNumber,
            manufacturer,
            certification,
            warranty
          )
          .send({ from: account })
          .on('transactionHash', function (hash) {
            console.log("Transaction hash:", hash);
          })
          .on('receipt', function (receipt) {
            console.log("Transaction successful, receipt:", receipt);
            window.location.reload(); // Reload page after product creation
          });
      } catch (error) {
        console.error("Error creating product:", error);
      }
    } else {
      console.log("Contract is not loaded.");
    }
  };

  return (
    <div>
      <Navbar account={account} />
      <div className="container">
        <h1>Supply Chain DApp (Electronics)</h1>
        <p>Your Account: {account || "Not connected"}</p>

        <h2>Create a New Electronic Product</h2>
        <input type="text" placeholder="Product Name" value={productName} onChange={(e) => setProductName(e.target.value)} />
        <input type="text" placeholder="Product Description" value={productDescription} onChange={(e) => setProductDescription(e.target.value)} />
        <input type="text" placeholder="Product Price (in ETH)" value={productPrice} onChange={(e) => setProductPrice(e.target.value)} />
        <input type="text" placeholder="Product Type (e.g., Smartphone, Microchip)" value={productType} onChange={(e) => setProductType(e.target.value)} />
        <input type="text" placeholder="Serial Number" value={serialNumber} onChange={(e) => setSerialNumber(e.target.value)} />
        <input type="text" placeholder="Manufacturer" value={manufacturer} onChange={(e) => setManufacturer(e.target.value)} />
        <input type="text" placeholder="Certification (e.g., CE, RoHS)" value={certification} onChange={(e) => setCertification(e.target.value)} />
        <input type="text" placeholder="Warranty" value={warranty} onChange={(e) => setWarranty(e.target.value)} />
        <button onClick={createProduct}>Create Product</button>

        <h2>Available Products</h2>
        <ul>
          {products.map((product, index) => (
            <li key={index}>
              <strong>{product.name}</strong> - {product.description} - {product.productType} - {product.serialNumber} - {Web3.utils.fromWei(product.price, 'Ether')} ETH
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
