import React, { useEffect, useState } from 'react';
import Web3 from 'web3';
import detectEthereumProvider from '@metamask/detect-provider';
import SupplyChain from './contracts/SupplyChain.json';
import Navbar from './components/navbar';

function App() {
  const [account, setAccount] = useState('');
  const [contract, setContract] = useState(null);
  const [productName, setProductName] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    loadWeb3();
    loadBlockchainData();
  }, []);

  // Load Web3 and connect MetaMask
  const loadWeb3 = async () => {
    const provider = await detectEthereumProvider();

    if (provider) {
      console.log("MetaMask detected");
      window.web3 = new Web3(provider);
      try {
        const accounts = await provider.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);
        console.log("Account connected:", accounts[0]);
        const networkId = await window.web3.eth.net.getId();
        console.log("Connected to network ID:", networkId);  // Check if connected to the correct network (should be 1337 for Ganache)
      } catch (error) {
        console.error("Error getting accounts from MetaMask:", error);
      }
    } else {
      alert('MetaMask is not installed. Please install MetaMask to interact with this DApp.');
    }
  };

  // Load blockchain data (contract, products)
  const loadBlockchainData = async () => {
    try {
      const web3 = window.web3;
      const accounts = await web3.eth.getAccounts();
      setAccount(accounts[0]);

      const networkId = await web3.eth.net.getId();
      const networkData = SupplyChain.networks[networkId];
      
      if (networkData) {
        const supplyChain = new web3.eth.Contract(SupplyChain.abi, networkData.address);
        setContract(supplyChain);
        console.log("Contract loaded:", supplyChain);

        const productCount = await supplyChain.methods.productCounter().call();
        console.log("Product count:", productCount);  // Log the number of products

        const loadedProducts = [];
        for (let i = 1; i <= productCount; i++) {
          try {
            const product = await supplyChain.methods.products(i).call();
            console.log("Product fetched:", product);  // Log each product
            loadedProducts.push(product);
          } catch (error) {
            console.error(`Error fetching product ${i}:`, error);
          }
        }
        setProducts(loadedProducts);
      } else {
        alert('Smart contract not deployed to the detected network.');
      }
    } catch (error) {
      console.error("Error loading blockchain data:", error);
    }
  };

  // Create a new product
  const createProduct = async () => {
    if (!contract) {
      console.log("Contract is not loaded.");
      return;
    }

    try {
      const weiPrice = Web3.utils.toWei(productPrice, 'Ether');
      console.log("Price in Wei:", weiPrice);  // Log the price converted to Wei
      console.log("Creating product with name:", productName);
      console.log("Description:", productDescription);

      const result = await contract.methods
        .createProduct(productName, productDescription, weiPrice)
        .send({ from: account })
        .on('transactionHash', function (hash) {
          console.log("Transaction hash:", hash);  // Log transaction hash
        })
        .on('receipt', function (receipt) {
          console.log("Transaction receipt:", receipt);  // Log transaction receipt
        });

      console.log("Transaction Result:", result);
      window.location.reload();  // Reload page after product creation
    } catch (error) {
      console.error("Error creating product:", error);
    }
  };

  return (
    <div>
      <Navbar account={account} />
      <div className="container">
        <h1>Supply Chain DApp</h1>
        <p>Your Account: {account || "Not connected"}</p>

        <h2>Create a New Product</h2>
        <input type="text" placeholder="Product Name" value={productName} onChange={(e) => setProductName(e.target.value)} />
        <input type="text" placeholder="Product Description" value={productDescription} onChange={(e) => setProductDescription(e.target.value)} />
        <input type="text" placeholder="Product Price (in ETH)" value={productPrice} onChange={(e) => setProductPrice(e.target.value)} />
        <button onClick={createProduct}>Create Product</button>

        <h2>Available Products</h2>
        <ul>
          {products.map((product, index) => (
            <li key={index}>
              <strong>{product.name}</strong> - {product.description} - {Web3.utils.fromWei(product.price, 'Ether')} ETH
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
