import React, { useEffect, useState } from "react";
import Web3 from "web3";
import SupplyChain from "./contracts/SupplyChain.json";  // ABI for the contract
import Navbar from "./components/navbar";  // Import the Navbar component

function App() {
    const [account, setAccount] = useState("");
    const [contract, setContract] = useState(null);
    const [web3, setWeb3] = useState(null);
    const [products, setProducts] = useState([]);
    const [productName, setProductName] = useState("");
    const [productDescription, setProductDescription] = useState("");
    const [productPrice, setProductPrice] = useState(0);

    useEffect(() => {
        loadWeb3();
        loadBlockchainData();
    }, []);

    const loadWeb3 = async () => {
        if (window.ethereum) {
            const web3 = new Web3(window.ethereum);
            setWeb3(web3);
            await window.ethereum.enable();
        } else if (window.web3) {
            const web3 = new Web3(window.web3.currentProvider);
            setWeb3(web3);
        } else {
            window.alert("Non-Ethereum browser detected. You should consider trying MetaMask!");
        }
    };

    const loadBlockchainData = async () => {
        const web3 = window.web3;
        const accounts = await web3.eth.getAccounts();
        setAccount(accounts[0]);

        const networkId = await web3.eth.net.getId();
        const networkData = SupplyChain.networks[networkId];
        if (networkData) {
            const supplyChain = new web3.eth.Contract(SupplyChain.abi, networkData.address);
            setContract(supplyChain);

            // Load all products
            const productCount = await supplyChain.methods.productCounter().call();
            const productsArray = [];
            for (let i = 1; i <= productCount; i++) {
                const product = await supplyChain.methods.products(i).call();
                productsArray.push(product);
            }
            setProducts(productsArray);
        } else {
            window.alert("SupplyChain contract not deployed to detected network.");
        }
    };

    const createProduct = async () => {
        if (contract) {
            await contract.methods
                .createProduct(productName, productDescription, web3.utils.toWei(productPrice, "Ether"))
                .send({ from: account });
            window.location.reload(); // Reload the page to reflect changes
        }
    };

    return (
        <div>
            {/* Add the Navbar and pass the account as a prop */}
            <Navbar account={account} />

            <div className="container">
                <h1>Supply Chain DApp</h1>
                <p>Your Account: {account}</p>

                <h2>Create a New Product</h2>
                <input
                    type="text"
                    placeholder="Product Name"
                    value={productName}
                    onChange={(e) => setProductName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Product Description"
                    value={productDescription}
                    onChange={(e) => setProductDescription(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Product Price in Ether"
                    value={productPrice}
                    onChange={(e) => setProductPrice(e.target.value)}
                />
                <button onClick={createProduct}>Create Product</button>

                <h2>Available Products</h2>
                <ul>
                    {products.map((product, index) => (
                        <li key={index}>
                            <p>Product ID: {product.id}</p>
                            <p>Name: {product.name}</p>
                            <p>Description: {product.description}</p>
                            <p>Price: {web3.utils.fromWei(product.price, "Ether")} Ether</p>
                            <p>Owner: {product.owner}</p>
                            <p>State: {product.state}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default App;
