import React, { useEffect, useState } from 'react';
import Web3 from 'web3';
import detectEthereumProvider from '@metamask/detect-provider';  // MetaMask detection
import SupplyChain from './contracts/SupplyChain.json';  // Import the contract ABI
import Navbar from './components/navbar';

function App() {
  const [account, setAccount] = useState('');
  const [contract, setContract] = useState(null);
  const [productName, setProductName] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    loadWeb3();
    loadBlockchainData();
  }, []);

  // Function to load Web3 and connect to MetaMask
  const loadWeb3 = async () => {
    try {
      const provider = await detectEthereumProvider();  // Check if MetaMask is installed

      if (provider) {
        console.log("MetaMask is detected");
        window.web3 = new Web3(provider);  // Initialize Web3 using MetaMask provider
        await provider.request({ method: 'eth_requestAccounts' });  // Request access to MetaMask accounts
      } else {
        alert("Please install MetaMask!");
      }
    } catch (error) {
      console.error("Failed to load Web3 or MetaMask: ", error);
    }
  };

  // Load blockchain data (accounts, contract, etc.)
  const loadBlockchainData = async () => {
    try {
      const web3 = window.web3;

      if (!web3) {
        console.log("Web3 is not initialized");
        return;
      }

      // Load the account from MetaMask
      const accounts = await web3.eth.getAccounts();
      if (accounts.length === 0) {
        alert("No accounts found. Please connect MetaMask.");
        return;
      }
      setAccount(accounts[0]);

      // Load the network and contract
      const networkId = await web3.eth.net.getId();
      const networkData = SupplyChain.networks[networkId];

      if (networkData) {
        const supplyChain = new web3.eth.Contract(SupplyChain.abi, networkData.address);
        setContract(supplyChain);

        // Load products from the contract
        const productCount = await supplyChain.methods.productCounter().call();
        const loadedProducts = [];
        for (let i = 1; i <= productCount; i++) {
          const product = await supplyChain.methods.products(i).call();
          loadedProducts.push(product);
        }
        setProducts(loadedProducts);
      } else {
        alert("Smart contract not deployed to detected network.");
      }
    } catch (error) {
      console.error("Error loading blockchain data: ", error);
    }
  };

  // Create a new product
  const createProduct = async () => {
    if (contract) {
      try {
        await contract.methods
          .createProduct(productName, productDescription, Web3.utils.toWei(productPrice, 'Ether'))
          .send({ from: account });
        window.location.reload();  // Reload the page to update the list of products
      } catch (error) {
        console.error("Error creating product: ", error);
      }
    }
  };

  return (
    <div>
      <Navbar account={account} />
      <div className="container">
        <h1>Supply Chain DApp</h1>
        <p>Your Account: {account || "Not connected"}</p>

        <h2>Create a New Product</h2>
        <input
          type="text"
          placeholder="Product Name"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Product Description"
          value={productDescription}
          onChange={(e) => setProductDescription(e.target.value)}
        />
        <input
          type="text"
          placeholder="Product Price (in ETH)"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
        <button onClick={createProduct}>Create Product</button>

        <h2>Available Products</h2>
        <ul>
          {products.map((product, index) => (
            <li key={index}>
              <strong>{product.name}</strong> - {product.description} - {Web3.utils.fromWei(product.price, 'Ether')} ETH
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
