import React, { useEffect, useState } from 'react';
import Web3 from 'web3';
import SupplyChain from './contracts/SupplyChain.json';
import Navbar from './components/navbar';

function App() {
  const [account, setAccount] = useState('');
  const [contract, setContract] = useState(null);
  const [productName, setProductName] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    loadWeb3();
    loadBlockchainData();
  }, []);

  // Load Web3 and enable MetaMask connection
  const loadWeb3 = async () => {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable(); // Request account access from MetaMask
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!');
    }
  };

  // Load blockchain data, including the connected account and the deployed smart contract
  const loadBlockchainData = async () => {
    const web3 = window.web3;

    // Get accounts from MetaMask
    const accounts = await web3.eth.getAccounts();
    setAccount(accounts[0]);

    // Get the network ID (from Ganache in this case)
    const networkId = await web3.eth.net.getId();
    const networkData = SupplyChain.networks[networkId];

    if (networkData) {
      // Get contract instance
      const supplyChain = new web3.eth.Contract(SupplyChain.abi, networkData.address);
      setContract(supplyChain);

      // Load products from the contract (if your contract stores product data)
      const productCount = await supplyChain.methods.productCounter().call();
      const loadedProducts = [];
      for (let i = 1; i <= productCount; i++) {
        const product = await supplyChain.methods.products(i).call();
        loadedProducts.push(product);
      }
      setProducts(loadedProducts);
    } else {
      window.alert('SupplyChain contract not deployed to detected network.');
    }
  };

  // Function to create a new product
  const createProduct = async () => {
    if (contract) {
      await contract.methods
        .createProduct(productName, productDescription, Web3.utils.toWei(productPrice, 'Ether'))
        .send({ from: account });
      window.location.reload(); // Reload the page to reflect changes
    }
  };

  return (
    <div>
      <Navbar account={account} />
      <div className="container">
        <h1>Supply Chain DApp</h1>
        <p>Your Account: {account}</p>

        <h2>Create a New Product</h2>
        <input
          type="text"
          placeholder="Product Name"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Product Description"
          value={productDescription}
          onChange={(e) => setProductDescription(e.target.value)}
        />
        <input
          type="text"
          placeholder="Product Price (in ETH)"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
        <button onClick={createProduct}>Create Product</button>

        <h2>Available Products</h2>
        <ul>
          {products.map((product, index) => (
            <li key={index}>
              <strong>{product.name}</strong> - {product.description} - {Web3.utils.fromWei(product.price, 'Ether')} ETH
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
